import { useState } from "react";

export default function App() {
  const [items, setitems] = useState([]);
  function handleaddItem(item) {
    setitems((items) => [...items, item]);
  }
  function deleteitem(id) {
    setitems((items) => items.filter((item) => item.id !== id));
  }
  <Show deleteitem={deleteitem} />;
  return (
    <div className="app">
      <Logo />
      <Form onAdd={handleaddItem} />
      <PackingList items={items} />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1> üõ©Ô∏èPack Your Bagsüåè</h1>;
}

function Form({ onAdd }) {
  function HandleSubmit(e) {
    e.preventDefault();
    if (!description) return;
    const newItem = { description, quantity, packed: false, id: Date.now() };
    onAdd(newItem);
    setdescription("");
    setquantity(1);
    console.log("Item added:", newItem);
  }

  const [description, setdescription] = useState("");
  const [quantity, setquantity] = useState(1);

  return (
    <form className="add-form" onSubmit={HandleSubmit}>
      <h3>Pick your things... üõí</h3>

      <select
        value={Number(quantity)}
        onChange={(e) => setquantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>

      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setdescription(e.target.value)}
      />

      <button type="submit">Add</button>
    </form>
  );
}

function PackingList({ items }) {
  return (
    <div className="list">
      <ul>
        {items.map((pro) => (
          <Show item={pro} key={pro.id} />
        ))}
      </ul>
    </div>
  );
}

function Show({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={deleteitem()}>‚ùå</button>
    </li>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>
        üîÜYou have xxx item in your list and you have packed xxx items in your
      </em>
    </footer>
  );
}
