// Learn how items are deleted
// learn how items are packed

import { useState } from "react";

export default function App() {
  const [items, setitems] = useState([]);

  function handleTogglePacked(id) {
    setitems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }
  function handleaddItem(item) {
    setitems((items) => [...items, item]);
  }

  function handleDeleteItem(id) {
    setitems((items) => items.filter((item) => item.id !== id));
  }
  return (
    <div className="app">
      <Logo />
      <Form onAdd={handleaddItem} />
      <PackingList
        items={items}
        onDelete={handleDeleteItem}
        ontoggle={handleTogglePacked}
      />
      <Stats count={items} />
    </div>
  );
}

function Logo() {
  return <h1> 🛩️Pack Your Bags🌏</h1>;
}

function Form({ onAdd }) {
  function HandleSubmit(e) {
    e.preventDefault();
    if (!description) return;
    const newItem = { description, quantity, packed: false, id: Date.now() };
    onAdd(newItem);
    setdescription("");
    setquantity(1);
    console.log("Item added:", newItem);
  }

  const [description, setdescription] = useState("");
  const [quantity, setquantity] = useState(1);

  return (
    <form className="add-form" onSubmit={HandleSubmit}>
      <h3>Pick your things... 🛒</h3>

      <select
        value={Number(quantity)}
        onChange={(e) => setquantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>

      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setdescription(e.target.value)}
      />

      <button type="submit">Add</button>
    </form>
  );
}

function PackingList({ items, onDelete, ontoggle }) {
  return (
    <div className="list">
      <ul>
        {items.map((pro) => (
          <Show
            item={pro}
            onDelete={onDelete}
            ontoggle={ontoggle}
            key={pro.id}
          />
        ))}
      </ul>
    </div>
  );
}

function Show({ item, onDelete, ontoggle }) {
  return (
    <li>
      <input
        type="checkbox"
        value={item.packed}
        onChange={() => ontoggle(item.id)}
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={() => onDelete(item.id)}>❌</button>
    </li>
  );
}

function Stats({ count }) {
  return (
    <footer className="stats">
      <em>
        🔆You have {count.length}item in your list and you have packed{" "}
        {count.length * 1}
        items in your
      </em>
    </footer>
  );
}
