import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: true },
  { id: 2, description: "Socks", quantity: 12, packed: false },
];

export default function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1> üõ©Ô∏èPack Your Bagsüåè</h1>;
}

function Form() {
  function HandleSubmit(e) {
    e.preventDefault();
  }
  const [description, setdescription] = useState("");
  const [quantity, setquantity] = useState(1);
  return (
    <form className="add-form" onSubmit={HandleSubmit}>
      <h3>Pick your things... üõí</h3>
      <select
        value={quantity}
        onChange={
          ((e) => setquantity(Number(e.target.value)),
          (e) => console.log(e.target.value))
        }
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>

      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setdescription(e.target.value)}
      />
      <button type="submit">Add</button>
    </form>
  );
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((pro) => (
          <Show item={pro} key={pro.id} />
        ))}
      </ul>
    </div>
  );
}

function Show({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button>‚ùå</button>
    </li>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>
        üîÜYou have xxx item in your list and you have packed xxx items in your
      </em>
    </footer>
  );
}
